// eslint-disable-next-line import/no-anonymous-default-export
export default (posts = [], action) =>{
    console.log(posts);
    switch (action.type) {
        case 'FETCH_ALL':
            return action.payload;
        case 'CREATE':
            return [...posts, action.payload];    
        default:
            return posts;
    }
}
