import React, { useState} from 'react';
import { Container} from '@material-ui/core';
import { BrowserRouter,Route, Routes ,Navigate} from 'react-router-dom';

import Home from './components/Home/Home';
import Navbar from './components/Navbar/Navbar';
import Auth from './components/Auth/Auth'
import PostDetails from './components/PostDetails/PostDetails';
import Create_post from './components/Form/Create_post';
import MainProfile from './components/Profile/MainProfile';
import Resources from './components/Resources/Resources';
const App = () => {
  const user = JSON.parse(localStorage.getItem('profile'));
  const [userstate, setuserstate] = useState(user)

  const setUserId = (val) => {
    setuserstate(val);
    // console.log(userstate);
  }

  return (
    <BrowserRouter>
      <Container maxWidth="lg">
        <Navbar setUserId={setUserId} />
        <Routes>
          <Route path="/profile/:id" element={<MainProfile userstate={userstate} setuserstate={setuserstate}/>} />
          <Route path="/" element={<Navigate to='/posts'/>} />
          {/* <Route path="/editprofile" element={<MainProfile/>} /> */}
          <Route path="/posts" element={<Home userstate={userstate} setuserstate={setuserstate}/>} />
          <Route path="/posts/search" element={<Home userstate={userstate} setuserstate={setuserstate}/>} />
          <Route path="/posts/:id" element={<PostDetails />} />
          <Route path="/auth" element={<Auth setUserId={setUserId}/>}/>
          <Route path="/addpost" element={<Create_post userstate={userstate} setuserstate={setuserstate} />} />
          <Route path="/resources/:id" element={<Resources userstate={userstate} setuserstate={setuserstate}/>} />


          {/* <Route path="/auth" element={ (user ? <Navigate to='/posts'/> : <Auth setUserId={setUserId}/> )}/> */}
        </Routes>
      </Container>
    </BrowserRouter>
  );
};

export default App;
