import React, { useState, useEffect } from 'react';
import { Card,CardActionArea,CardContent ,CardMedia, Typography, Toolbar, Avatar, Button, Paper} from '@material-ui/core';
// import { useNavigate, useLocation } from 'react-router-dom';
// import { useDispatch } from 'react-redux';
import useStyles from './styles';

import styled, { ThemeProvider } from "styled-components";
import ReactDOM from "react-dom";
import IconButton from "@material-ui/core/IconButton";
import AvatarGroup from "@material-ui/lab/AvatarGroup";
import { StylesProvider, useTheme } from "@material-ui/core/styles";

const Content = styled.div`
  margin: auto;
  max-width: 500px;
`;


const AvatarContainer = styled.div`
  display: flex;
  margin-bottom: 14px;
  & > * {
    margin: 4px;
  }
`;

const SizedAvatar = styled(Avatar)`
  ${({ size, theme }) => `
    width: ${theme.spacing(size)}px; 
    height: ${theme.spacing(size)}px; 
  `};
`;


const AvatarLabel = styled.div`
  display: flex;
  align-items: center;
`;



const MainProfile = () => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    // const dispatch = useDispatch();
    // const location = useLocation();
    // const navigate = useNavigate();
    const classes = useStyles();
    console.log(user?.result?.imageUrl)
    return (
        <div>
            <Card className={classes.card} raised elevation={6}>
                <CardMedia className={classes.media} image={user?.result?.imageUrl} />
                <div className={classes.overlay}>
                    {/* <img src={user?.result?.imageUrl}/> */}
                    <Typography variant="h6">{user.name}</Typography>
                </div>
                <div className={classes.details}>
                    <Typography variant="body2" color="textSecondary" component="h2">{user?.result?.imgUrl}</Typography>
                </div>
                <Typography className={classes.title} gutterBottom variant="h5" component="h2">Institude</Typography>
                <CardContent>
                    <Typography variant="body2" color="textSecondary" noWrap={true} component="p">Posts</Typography>
                </CardContent>
            </Card>
        </div>
    )
}

export default MainProfile;



function SizedAvatars() {
  return (
    <AvatarContainer>
      <SizedAvatar
        size={4}
        alt="Jack Sparrow"
        src="https://images.pexels.com/photos/6386956/pexels-photo-6386956.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
      >
        G
      </SizedAvatar>
      <SizedAvatar
        size={6}
        alt="Mike Wazowski"
        src="https://images.pexels.com/photos/1722198/pexels-photo-1722198.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
      >
        B
      </SizedAvatar>
      <SizedAvatar
        size={16}
        alt="Evelyn Carnahan"
        src="https://images.pexels.com/photos/4016173/pexels-photo-4016173.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
      >
        O
      </SizedAvatar>
    </AvatarContainer>
  );
}

function App() {
  const theme = useTheme();
  return (
    <StylesProvider injectFirst>
      <ThemeProvider theme={theme}>
        <Content>
          <Typography variant="h5"> Image Avatars </Typography>
          <ImageAvatars />
          <Typography variant="h5"> Letter Avatars </Typography>
          <LetterAvatars />
          <Typography variant="h5"> Avatar with Text </Typography>
          <AvatarWithText />
          <Typography variant="h5"> Colored Avatars </Typography>
          <ColoredAvatars />
          <Typography variant="h5"> Sized Avatars </Typography>
          <SizedAvatars />
          <Typography variant="h5"> Bordered Avatars </Typography>
          <BorderedAvatars />
          <Typography variant="h5"> Fallback Avatar </Typography>
          <FallbackAvatars />
          <Typography variant="h5"> Avatar Group </Typography>
          <AvatarGroupExample />
          <Typography variant="h5"> Avatar Button </Typography>
          <AvatarButton />
        </Content>
      </ThemeProvider>
    </StylesProvider>
  );
}

ReactDOM.render(<App />, document.querySelector("#app"));
