import * as React from 'react';
import PropTypes from 'prop-types';
import { styled } from '@mui/material/styles';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';

import { withStyles, MenuItem } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import Divider from "@material-ui/core/Divider";
import Icon from "@material-ui/core/Icon";
import { teal, grey } from "@material-ui/core/colors";


import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';
import { useNavigate } from 'react-router-dom';
import { createPost, updatePost } from '../../actions/posts';
import ChipInput from 'material-ui-chip-input';
import useStyles from './formStyle';


const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': {
    padding: theme.spacing(2),
  },
  '& .MuiDialogActions-root': {
    padding: theme.spacing(1),
  },
}));

const BootstrapDialogTitle = (props) => {
  const { children, onClose, ...other } = props;

  return (
    <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
      {children}
      {onClose ? (
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </DialogTitle>
  );
};

BootstrapDialogTitle.propTypes = {
  children: PropTypes.node,
  onClose: PropTypes.func.isRequired,
};


const styles = theme => ({
  root: {
    flexGrow: 1
  },
  primaryColor: {
    color: teal[500]
  },
  secondaryColor: {
    color: grey[700]
  },

  padding: {
    padding: 0
  },
  mainHeader: {
    backgroundColor: grey[100],
    padding: 20,
    alignItems: "center"
  },
  mainContent: {
    padding: 40
  },
  secondaryContainer: {
    padding: "20px 25px",
    backgroundColor: grey[200]
  }
});

export default function CustomizedDialogs(props) {
  const [open, setOpen] = React.useState(false);
  const classes = styles;
  const [values, setValues] = React.useState({
      message: "",
      title: "",
      tags: [],
  });

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };


  ///FORM WORK

  const [postData, setPostData] = useState({ title: '', message: '', tags: [], selectedFile: '' });
  const post = useSelector((state) => (currentId ? state.posts.posts.find((message) => message._id === currentId) : null));
  const dispatch = useDispatch();
  const classes1 = useStyles();
  const user = JSON.parse(localStorage.getItem('profile'));
  const history = useNavigate();
  // clears the post form to it's default state
  const clear = () => {
    setCurrentId(0);
    setPostData({ title: '', message: '', tags: [], selectedFile: '' });
  };
  //populating the form with post data to update
  useEffect(() => {
    if (!post?.title) clear();
    if (post) setPostData(post); // setting the new data to which got from the form const[postData,setPostData]
  }, [post]);
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (currentId === 0) {
      dispatch(createPost({ ...postData, name: user?.result?.name }, history));
      clear();
    } else {
      dispatch(updatePost(currentId, { ...postData, name: user?.result?.name }));
      for (let i = 0; i < 1000000; i++){
        
      }
      clear();
      history(`/posts/${currentId}`)
    }
  };

  return (
    <div>
       <MenuItem variant="fill" onClick={handleClickOpen}>Edit</MenuItem>
      <BootstrapDialog
        onClose={handleClose}
        aria-labelledby="customized-dialog-title"
        open={open}
      >
        <BootstrapDialogTitle id="customized-dialog-title" onClose={handleClose}>
          
        </BootstrapDialogTitle>
        <DialogContent dividers>
          <Grid container>
                  <Grid item xs={8}>
                      <Grid container direction="row" className={classes.mainHeader}>
                          <Grid item xs={8}>
                              <Typography className={classes.primaryColor} variant="h5">
                                  Edit Post
                              </Typography>
                          </Grid>
                      </Grid>
                      <Grid container direction="row" className={classes.mainContent} spacing={1} >
                          <Grid item xs={12}>
                              <TextField fullWidth margin="dense" variant="outlined" label="Title" id="title" value={values.title} onChange={(e) => setValues({ ...values, title: e.target.value })} />
                          </Grid>
                          <Grid item xs={12}>
                              <TextField fullWidth multiline rows="6" margin="dense" variant="outlined" label="Message" id="message" value={values.message} onChange={(e) => setValues({ ...values, message: e.target.value })} />
                          </Grid>
                          <Grid item xs={12}>
                              <TextField fullWidth margin="dense" variant="outlined" label="Tags (Comma separated)" id="tags" value={values.tags} onChange={(e) => setValues({ ...values, tags: e.target.value })} />
                          </Grid>
                          <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => console.log('hi')} /></div>
           
                      </Grid>
                  </Grid>
              </Grid>


        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose}>
            Save changes
          </Button>
        </DialogActions>
      </BootstrapDialog>
    </div>
  );
}
