import useStyles from './styles';

const Create_post = ({ post ,userstate}) => {
    const [currentId, setCurrentId] = useState(0);
    const user = JSON.parse(localStorage.getItem('profile'));
    const classes = useStyles();

    const toAuthPage = () =>{

    };
