import React, { useEffect, useState } from 'react';
import { Card, CardActions, CardContent, Paper, Typography, CircularProgress, Divider, Button, Menu, MenuItem } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { useParams, useNavigate } from 'react-router-dom';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import { getPost, getPostsBySearch } from '../../actions/posts';
import useStyles from './styles';
import CommentSection from './CommentSection';
import { likePost, deletePost } from '../../actions/posts';

import CustomizedDialogs from './Dailog';
import EditForm from './EditForm';

const Post = () => {
  const { post, posts, isLoading } = useSelector((state) => state.posts);
  const dispatch = useDispatch();
  const history = useNavigate();
  const classes = useStyles();
  const { id } = useParams();
  const [dailopen, setdailOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };

  const changeVal = () => {
    console.log('hi')
    setdailOpen(true)
    console.log(dailopen)
  }
  const handleCloseEdit = () => {
    changeVal()
    // alert(dailopen)
    setAnchorEl(null);
  };

  const handleCloseDelete = () => {
    setAnchorEl(null);
    dispatch(deletePost(post._id));
    history('/')
  }
  const handleClose = () => {
    setAnchorEl(null);
  }

  const handleCloseHome = () => {
    setAnchorEl(null);
    history('/')
  };



  useEffect(() => {
    dispatch(getPost(id));
  }, [id]);

  useEffect(() => {
    if (post) {
      dispatch(getPostsBySearch({ search: 'none', tags: post?.tags.join(',') }));
    }
  }, [post]);

  if (!post) return null;

  const openPost = (_id) => history(`/posts/${_id}`);

  if (isLoading) {
    return (
      <div>
        <CircularProgress size="3em" style={{ color: '#ff675c', position: 'absolute' }} />
        <Paper elevation={6} className={classes.loadingPaper}>
          <Card elevation={6} style={{ backgroundColor: '#ebe5ff', borderRadius: '10px' }}> <Typography style={{ margin: '10px 10px' }} variant="h4">Loading....</Typography></Card>
        </Paper>
      </div>
    );
  }

  const recommendedPosts = posts.filter(({ _id }) => _id !== post._id);
  const user = JSON.parse(localStorage.getItem('profile'));


  return (
    <Paper style={{ padding: '20px', borderRadius: '15px' }} elevation={6}>
      <div className={classes.card}>
        <div className={classes.section}>
          {(user?.result?.googleId === post?.creator || user?.result?._id === post?.creator) ? (
            <div >
              <Typography variant="h3" component="h2">{post.title}
                <Button onClick={handleClick}
                  style={{ color: '#334155' }}
                  size="small"
                >
                  <MoreHorizIcon fontSize="medium" />
                </Button>
              </Typography>
              <Menu
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleCloseEdit, handleCloseDelete, handleCloseHome}
              >

                <CustomizedDialogs setAnchorEl={setAnchorEl} title={"Edit"} children={<EditForm currentId={0} setCurrentId={post._id} />}></CustomizedDialogs>
                <MenuItem onClick={handleCloseDelete}>Delete</MenuItem>
                <MenuItem onClick={handleCloseHome}>Back to Home</MenuItem>
                <MenuItem onClick={handleClose}>Close</MenuItem>
              </Menu>
            </div>
          ) : (
            <div >
              <Typography variant="h3" component="h2">{post.title}
                <Button onClick={handleClick}
                  style={{ color: '#334155' }}
                  size="small"
                >
                  <MoreHorizIcon fontSize="medium" />
                </Button>
              </Typography>
              <Menu
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleCloseEdit, handleCloseDelete, handleCloseHome}
              >
                <MenuItem onClick={handleCloseHome}>Back to Home</MenuItem>
                <MenuItem onClick={handleClose}>Close</MenuItem>
              </Menu>
            </div>
          )}

          <Typography gutterBottom variant="h6" color="textSecondary" component="h2">{post.tags.map((tag) => `#${tag} `)}</Typography>
          <Typography gutterBottom variant="body1" component="p">{post.message}</Typography>
          <Typography variant="h6">Created by: {post.name}</Typography>
          <Typography variant="body1">{moment(post.createdAt).fromNow()}</Typography>
          {/* <Divider style={{ margin: '20px 0' }} /> */}
          {/* <Typography variant="body1"><strong>Realtime Chat - coming soon!</strong></Typography> */}
          <Divider style={{ margin: '20px 0' }} />
          {/* <Typography variant="body1"><strong>Comments - coming soon!</strong></Typography> */}
          <CommentSection post={post} />
          <Divider style={{ margin: '20px 0' }} />
        </div>
        {post.selectedFile ?
          (<div className={classes.imageSection}>
            <img className={classes.media} src={post.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} alt={post.title} />
          </div>) : <div></div>}
        {/* <div className={classes.imageSection}> */}
        {/* <img className={classes.media} src={post.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} alt={post.title} /> */}
        {/* </div> */}
      </div>
      {!!recommendedPosts.length && (
        <div className={classes.section}>
          <Typography gutterBottom variant="h5">You might also like:</Typography>
          <Divider />
          <div className={classes.recommendedPosts}>

            {recommendedPosts.map(({ title, name, message, likes, selectedFile, _id }) => (
              <Card className={classes.card} style={{ backgroundColor: "#ffffff", margin: "5px 5px" }} elevation={6}>

                <div style={{ width: "100%", height: "100%", margin: '20px', cursor: 'pointer' }} onClick={() => openPost(_id)} key={_id}>
                  <Typography gutterBottom variant="h6" style={{ fontWeight: 'bold' }}>{title}</Typography>
                  <Typography gutterBottom variant="subtitle2">{name}</Typography>
                  <CardContent>
                    <Typography variant="subtitle2" color="textSecondary" noWrap={true}  >{message}</Typography>
                  </CardContent>
                  <Typography gutterBottom style={{ fontWeight: 'bold' }} variant="subtitle1">Likes: {likes.length}</Typography>
                  {/* <img src={selectedFile} width="200px" /> */}
                </div>
              </Card>
            ))}
          </div>


        </div>
      )}
    </Paper>
  );
};

export default Post;
