import { START_LOADING,GET_USER_BY_ID } from '../constants/actionTypes';
import * as api from '../api/index.js';
import { getUsersById } from '../api/index.js';
export const getUserById = (id) => async (dispatch) => {
  try {
    dispatch({ type: START_LOADING });
    console.log("Hi in the action")
    const { userData } = await getUsersById(id);
    // console.log("Hi")
    console.log(userData)
    dispatch({ type: GET_USER_BY_ID, payload: { userData: userData } });
  } catch (error) {
    console.log(error);
  }
};
