import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';
import { useNavigate } from 'react-router-dom';
import { createPost, updatePost } from '../../actions/posts';


const EditForm = ()=> {
    return (
      
        <Paper>
          <form autoComplete="off" noValidate onSubmit={handleSubmit}>
           <Typography variant="h6">Edit Form</Typography>
                <TextField name="title" variant="outlined" label="Title" fullWidth value={""} />
           <TextField name="message" variant="outlined" label="Message" fullWidth multiline rows={4} value={""} />
           <TextField name="tags" variant="outlined" label="Tags (coma separated)" fullWidth value={""} />
            <div ><FileBase type="file" multiple={false}/></div>
           <Button  variant="contained" size="large" type="submit" fullWidth>Submit</Button>
           <Button variant="contained"  size="small" onClick={clear} fullWidth>Clear</Button>
          </form>
        </Paper>
  );
}

export default EditForm;
